import java.util.Objects;/** * Represents an Account with it's details - * the Individual who owns the Account and the Amount of * money in the Account * * @Author Goch */public class Account {  private Individual accountHolder;  private Amount currentAccountBalance;  /**   * Creates a new Account given the account holder   * and the current account balance.   *   * @param accountHolder   * @param currentAccountBalance   */  public Account(Individual accountHolder, Amount currentAccountBalance){    this.accountHolder = accountHolder;    this.currentAccountBalance = currentAccountBalance;  }  /**   *   * @return the account holder   */  public Individual getAccountHolder() {    return accountHolder;  }  /**   *   * @return the current account balance   */  public Amount getCurrentAccountBalance() {    return currentAccountBalance;  }  /**   * Returns a new Account showing an updated current account balance with the deposit amount added.   * @param depositAmount   * @return a new Account   */  public Account deposit(Amount depositAmount){    Amount balance = getCurrentAccountBalance();    Integer balanceDollars = balance.getDollars();    Integer balanceCents = balance.getCents();    Integer depositDollars = depositAmount.getDollars();    Integer depositCents = depositAmount.getCents();    Integer updatedBalanceDollars = new Integer (balanceDollars.intValue()        + depositDollars.intValue());    Integer updatedBalanceCents = new Integer (balanceCents.intValue()        + depositCents.intValue());    if(updatedBalanceCents.intValue() > 99){      updatedBalanceDollars = new Integer(updatedBalanceDollars.intValue() + 1);      updatedBalanceCents = new Integer(updatedBalanceCents.intValue() - 100);    }    Amount updatedBalance = new Amount(updatedBalanceDollars, updatedBalanceCents);    return new Account(getAccountHolder(), updatedBalance);  }  /**   * Returns a new Account showing an updated current account balance with the deposit amount added.   * @param withdrawalAmount   * @return a new Account   */  public Account withdraw(Amount withdrawalAmount) {    Amount balance = getCurrentAccountBalance();    Integer balanceDollars = balance.getDollars();    Integer balanceCents = balance.getCents();    Integer withdrawalDollars = withdrawalAmount.getDollars();    Integer withdrawalCents = withdrawalAmount.getCents();    Integer updatedBalanceDollars = new Integer(balanceDollars.intValue()        - withdrawalDollars.intValue());    Integer updatedBalanceCents = new Integer(balanceCents.intValue()        - withdrawalCents.intValue());    if (updatedBalanceCents.intValue() < 0) {      updatedBalanceDollars = new Integer(updatedBalanceDollars.intValue() - 1);      updatedBalanceCents = new Integer(updatedBalanceCents.intValue() + 100);    }    Amount updatedBalance = new Amount(updatedBalanceDollars, updatedBalanceCents);    return new Account(getAccountHolder(), updatedBalance);  }}